import cls from './GameInfo.module.scss';
import FolderIcon from '../../../assets/icons/Folder.svg';
import AvatarIcon from '../../../assets/icons/Game.svg';
import CloseIcon from '../../../assets/icons/Cross.svg';
import { useState } from 'react';

export const GameInfo = () => {
  const [displayNone, setDispalyNone] = useState('');
  const None = 'display: none';

  return (
    <div className={cls.GameInfo}>
      <div className={cls.infoContainer}>
        <div className={cls.header}>
          <div className={cls.headerItem}>
            <FolderIcon className={cls.folderIcon} />
            <h2>Игры</h2>
            <AvatarIcon />
            <h2>Название игры</h2>
          </div>
          <CloseIcon
            className={cls.CloseIcon}
            onClick={() => setDispalyNone(displayNone)}
            style={displayNone}
          />
        </div>
        <div className={cls.container}>
          <AvatarIcon className={cls.avatarIcon} />
          <div className={cls.description}>description</div>
          <p>
            Описание описание описание описание описание описание описание описание описание
            описание описание описание описание описание описание описание описание описание
            описание описание
          </p>
          <div className={cls.choose}>
            <div>Читать</div>
            <div className={cls.choosePlay}>Играть</div>
          </div>
        </div>
      </div>
    </div>
  );
};
